function vecout = EDstrpblnk(vecin,option)% Removes initial or trailing blanks from a text string.% If the optional input argument option = 'all', all blanks will be removed.% If option = 'dou', all internal double spaces will be replaced by single spaces.%% Peter Svensson (peter.svensson@ntnu.no) 18 Jan 2018%% vecout = EDstrpblnk(vecin,option);% 970921 Functioning version% 980101 Employs deblank% 980104 Skipped deblank - it always needs a string.% 980404 Skipped blanks and ascii 0% 18 Jan 2018 Copied to EDtoolboxif nargin < 2	option = 0;else	if length(option) < 3		option = 0;	    else		option = lower(option(1:3));		if option == 'all'			option = 1;		elseif option == 'dou'			option = 2;        else			option = 0;		end	endendvecout = vecin;ready = 0;while ready == 0	nchars = length(vecout);	iv1 = find(vecout == 32 | vecout == 0);	if isempty(iv1)		ready = 1;	    else		blnkstart = iv1(1) == 1;		blnkend = iv1( length(iv1) ) == nchars;		if blnkstart == 0 && blnkend == 0			ready = 1;        else				if blnkend				vecout = vecout(1:nchars-1);				iv1  = iv1(1:length(iv1)-1);			end			if blnkstart				vecout = vecout(2:length(vecout));					end		end	endendif option == 1	ivec = find(vecout== 32);	if ~isempty(ivec)		vecout(ivec) = [];		endelseif option == 2	n = length(vecout);	ivec = find(vecout(1:n-1) == 32 & vecout(2:n) == 32);	if ~isempty(ivec)		vecout(ivec) = [];	endend